import AcercaDe from './AcercaDe';
import PropTypes from 'prop-types';
import './ContenedorTarjetas.css';
import Productos from './productos';
import Galeria from './Galeria';
import Sucursales from './Sucursales';
import Contacto from './Contacto';
function ContenedorTarjetas({vista}) {
    const vistas = {
    "Inicio": <Inicio/>,
    "AcercaDe": <AcercaDe/>,
    "Productos": <Productos/>,
    "Galeria": <Galeria/>,
    "Sucursales": <Sucursales />,
    "Contacto": <Contacto/>
    }
  return (
    <div className='contenedorDiv'>
         {vistas[vista] || <Inicio />}
    </div>
  );
}

function Inicio(){
    return(
    <>
        <TarjetaComponent titulo="Java" descripcion = "lenguaje de programación de propósito general, orientado a objetos y multiplataforma. El código compilado puede ejecutarse en cualquier dispositivo que tenga una Máquina Virtual Java" imagen="data:image/jpeg;base64,"/>
        <TarjetaComponent titulo="Python" descripcion = " lenguaje de programación de alto nivel, interpretado y de código abierto, famoso por su sintaxis clara y legible que se asemeja al inglés AWS" imagen="data:image/jpeg;base64,"/>
        <TarjetaComponent titulo="JavaScript" descripcion = "JavaScript es un lenguaje de programación ligero, interpretado y orientado a objetos BYJU'S, diseñado principalmente para añadir interactividad y dinamismo a las páginas web " imagen="data:image/jpeg;base64,"/>
        <TarjetaComponent titulo="PHP" descripcion = "lenguaje de código abierto muy popular, diseñado específicamente para el desarrollo web y que se ejecuta en el lado del servidor PHP" imagen="data:image/jpeg;base64,"/>
    </>
    )
}

function TarjetaComponent(props){
    return(
        <div className='tarjetaDiv'>
            <img src={props.imagen} alt='Logotipo'/>
            <h3>{props.titulo}</h3>
            <p>{props.descripcion}</p>
            <a href='#'>Ver más</a>
        </div>
    )
}

ContenedorTarjetas.propTypes = {
  vista: PropTypes.string.isRequired
};
export default ContenedorTarjetas;